import React, {  useEffect,useState } from 'react';
import './Head.css';  // Ensure this file is correctly imported
 

const Head = () => {
  const [showNav, setShowNav] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > window.innerHeight * 0.8) { 
        setShowNav(true);  // Show the nav with fade-in animation
      } else {
        setShowNav(false);  // Hide the nav if user scrolls back to top
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <section className="header">
      
      
      <div className={`nav-bar ${showNav ? 'visible' : 'hidden'}`}> 
        <nav>
          <ul className="nav-links">
          <li><a href="#home">HOME</a></li>

            <li><a href="#about">ABOUT</a></li>
            <li><a href="#work">PROJECTS</a></li>
            <li><a href="#skills">SKILLS</a></li>
            <li><a href="#contact">CONTACT</a></li>
          </ul>
        </nav>
      </div>
    </section>
  );
};

export default Head;
