import React, { useEffect, useState, useRef } from 'react';
import './Head.css'; 


const Head = () => {
  const [showNav, setShowNav] = useState(false);
  const [activeSection, setActiveSection] = useState('home');
  
  // Refs for each section
  const aboutRef = useRef(null);
  const WorksRef = useRef(null);
  const skillsRef = useRef(null);
  const contactRef = useRef(null);

  // Show nav when scrolling past a certain point
  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > window.innerHeight * 0.8) { 
        setShowNav(true);  // Show the nav when scrolled past 80% of the first page
      } else {
        setShowNav(false);  // Hide the nav if the user scrolls back to top
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Detect section in view and update the active nav link
  useEffect(() => {
    const sections = [
      { id: 'home', ref: null },   // Home is the default
      { id: 'about', ref: aboutRef },
      { id: 'work', ref: WorksRef },
      { id: 'skills', ref: skillsRef },
      { id: 'contact', ref: contactRef },
    ];

    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setActiveSection(entry.target.id);  // Update active section
          }
        });
      },
      { threshold: 0.6 }  // Trigger when 60% of the section is in view
    );

    // Observe each section
    sections.forEach((section) => {
      if (section.ref && section.ref.current) {
        observer.observe(section.ref.current);
      }
    });

    return () => {
      sections.forEach((section) => {
        if (section.ref && section.ref.current) {
          observer.unobserve(section.ref.current);
        }
      });
    };
  }, []);

  return (
    <section>
      {/* Conditionally render the nav bar with fade-in animation */}
      <div className={`nav-bar ${showNav ? 'visible' : 'hidden'}`}> 
        <nav>
          <ul className="nav-links">
            <li>
              <a href="#home" className={activeSection === 'home' ? 'active' : ''}>HOME</a>
            </li>
            <li>
              <a href="#about" className={activeSection === 'about' ? 'active' : ''}>ABOUT</a>
            </li>
            <li>
              <a href="#work" className={activeSection === 'work' ? 'active' : ''}>PROJECTS</a>
            </li>
            <li>
              <a href="#skills" className={activeSection === 'skills' ? 'active' : ''}>SKILLS</a>
            </li>
            <li>
              <a href="#contact" className={activeSection === 'contact' ? 'active' : ''}>CONTACT</a>
            </li>
          </ul>
        </nav>
      </div>
      <about ref={aboutRef} />
    </section>
  );
};

export default Head;
