import React, {  useEffect,useState } from 'react';
import './Head.css';  // Ensure this file is correctly imported
 

const Head = () => {
  const [showNav, setShowNav] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > window.innerHeight * 0.8) { // Show nav after scrolling past 80% of viewport height
        setShowNav(true);
      } else {
        setShowNav(false);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <section className="header">
      <div className="header-content">
        <h1>Hello, I'm <span className='myname'>HASINI MIRYALA.</span></h1>
        <p>I'm a Full-Stack Web Developer.</p>
      </div>
       
      <div class="snow_wrap">
	      <div class="snow"></div>
      </div>
      {showNav &&(
      <div className={`nav-bar ${showNav ? 'visible' : 'hidden'}`}> 
        <nav>
          <ul className="nav-links">
            <li><a href="#about">About</a></li>
            <li><a href="#work">Projects</a></li>
            <li><a href="#skills">Skills</a></li>
            <li><a href="#contact">Contact</a></li>
          </ul>
        </nav>
      </div>)}
    </section>
  );
};

export default Head;
