import React from 'react';
import './Blog.css';

const Blog = () => {
  const blogs = [
    {
      title: "Understanding JavaScript Closures",
      description: "A deep dive into one of JavaScript’s core concepts.",
      image: "path/to/image1.jpg",
      link: "#"
    },
    {
      title: "CSS Grid Layouts Explained",
      description: "Learn how to create complex layouts easily with CSS Grid.",
      image: "path/to/image2.jpg",
      link: "#"
    },
    {
      title: "React Hooks: A Complete Guide",
      description: "Master the power of hooks to manage state and effects.",
      image: "path/to/image3.jpg",
      link: "#"
    },
    // Add more blogs as needed
  ];

  return (
    <section className="blog" id="blog">
      <h2>My Blogs</h2>
      <div className="blog-grid">
        {blogs.map((blog, index) => (
          <div className="blog-item" key={index}>
            <img src={blog.image} alt={blog.title} className="blog-image" />
            <h3>{blog.title}</h3>
            <p>{blog.description}</p>
            <a href={blog.link} className="read-more">Read more</a>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Blog;
